mapper.select:
select
circleId, labelId, userId, circleName, circleDescription, circleImagePath, submitDate, circleStatus
from circle_blue.circleinfo


        and circleId = #{circleid}


        and labelId = #{labelid}


        and userId = #{userid}


        and circleName = #{circlename}


        and circleDescription = #{circledescription}


        and circleImagePath = #{circleimagepath}


        and submitDate = #{submitdate}


        and circleStatus = #{circlestatus}
